const node_openssl = require('../index.js');
var fs = require('fs');

var options = {
	binpath: 'C:/Program Files/OpenSSL-Win64/bin/openssl.exe'
}

var openssl = new node_openssl(options);

fs.readFile('./ca.crt', function(err, ca) {
	fs.readFile('./8DFDDA1DDC9014C6.crt', function(err, cert) {
		openssl.getOCSPHashes(ca.toString(), cert.toString(), 'sha512', function(err, ocsp, cmd){
			if(err) {
				console.log(err);
			} else {
				console.log(ocsp);
				//console.log(cmd);
			}
		});
	});
});
